(function ($) {
    "use strict";

    // Function to set the sidebar state in localStorage
    function setSidebarState(isToggled) {
        localStorage.setItem("sidebarToggled", isToggled);
    }

    // Function to get the sidebar state from localStorage
    function getSidebarState() {
        return localStorage.getItem("sidebarToggled") === "true";
    }

    // Apply the saved state on page load
    $(document).ready(function () {
        if (getSidebarState()) {
            $("body").addClass("sidebar-toggled");
            $(".sidebar").addClass("toggled");
        }
    });

    $("#sidebarToggle, #sidebarToggleTop").on("click", function (e) {
        $("body").toggleClass("sidebar-toggled");
        $(".sidebar").toggleClass("toggled");

        if ($(".sidebar").hasClass("toggled")) {
            $(".sidebar .collapse").collapse("hide");
            setSidebarState(true); // Save the toggled state
        } else {
            setSidebarState(false); // Save the toggled state
        }
    });

    $(window).resize(function () {
        if ($(window).width() < 768) {
            $(".sidebar .collapse").collapse("hide");
        }
        if (
            !$(".sidebar").hasClass("toggled") &&
            $(window).width() < 480
        ) {
            $("body").addClass("sidebar-toggled");
            $(".sidebar").addClass("toggled");
            $(".sidebar .collapse").collapse("hide");
        }
    });

    $("body.fixed-nav .sidebar").on(
        "mousewheel DOMMouseScroll wheel",
        function (e) {
            var o;
            if ($(window).width() > 768) {
                o = e.originalEvent.wheelDelta || -e.detail;
                this.scrollTop += 30 * (o < 0 ? 1 : -1);
                e.preventDefault();
            }
        }
    );

    $(document).on("scroll", function () {
        if ($(this).scrollTop() > 100) {
            $(".scroll-to-top").fadeIn();
        } else {
            $(".scroll-to-top").fadeOut();
        }
    });

    $(document).on("click", "a.scroll-to-top", function (e) {
        var o = $(this);
        $("html, body")
            .stop()
            .animate(
                { scrollTop: $(o.attr("href")).offset().top },
                1000,
                "easeInOutExpo"
            );
        e.preventDefault();
    });
})(jQuery);
